contracts/WadRayMath.sol.content:
// SPDX-License-Identifier: agpl-3.0
pragma solidity 0.8.12;

import {MathErrors} from "./MathErrors.sol";

/**
 * @title WadRayMath library
 * @author Aave
 * @dev Provides mul and div function for wads (decimal numbers with 18 digits precision) and rays (decimals with 27 digits)
 **/

library WadRayMath {
  uint256 internal constant WAD = 1e18;
  uint256 internal constant halfWAD = WAD / 2;

  uint256 internal constant RAY = 1e27;
  uint256 internal constant halfRAY = RAY / 2;

  uint256 internal constant WAD_RAY_RATIO = 1e9;

  /**
   * @return One ray, 1e27
   **/
  function ray() internal pure returns (uint256) {
    return RAY;
  }

  /**
   * @return One wad, 1e18
   **/

  function wad() internal pure returns (uint256) {
    return WAD;
  }

  /**
   * @return Half ray, 1e27/2
   **/
  function halfRay() internal pure returns (uint256) {
    return halfRAY;
  }

  /**
   * @return Half ray, 1e18/2
   **/
  function halfWad() internal pure returns (uint256) {
    return halfWAD;
  }

  /**
   * @dev Multiplies two wad, rounding half up to the nearest wad
   * @param a Wad
   * @param b Wad
   * @return The result of a*b, in wad
   **/
  function wadMul(uint256 a, uint256 b) internal pure returns (uint256) {
    if (a == 0 || b == 0) {
      return 0;
    }

    require(a <= (type(uint256).max - halfWAD) / b, MathErrors.MATH_MULTIPLICATION_OVERFLOW);

    return (a * b + halfWAD) / WAD;
  }

  /**
   * @dev Divides two wad, rounding half up to the nearest wad
   * @param a Wad
   * @param b Wad
   * @return The result of a/b, in wad
   **/
  function wadDiv(uint256 a, uint256 b) internal pure returns (uint256) {
    require(b != 0, MathErrors.MATH_DIVISION_BY_ZERO);
    uint256 halfB = b / 2;

    require(a <= (type(uint256).max - halfB) / WAD, MathErrors.MATH_MULTIPLICATION_OVERFLOW);

    return (a * WAD + halfB) / b;
  }

  /**
   * @dev Multiplies two ray, rounding half up to the nearest ray
   * @param a Ray
   * @param b Ray
   * @return The result of a*b, in ray
   **/
  function rayMul(uint256 a, uint256 b) internal pure returns (uint256) {
    if (a == 0 || b == 0) {
      return 0;
    }

    require(a <= (type(uint256).max - halfRAY) / b, MathErrors.MATH_MULTIPLICATION_OVERFLOW);

    return (a * b + halfRAY) / RAY;
  }

  /**
   * @dev Divides two ray, rounding half up to the nearest ray
   * @param a Ray
   * @param b Ray
   * @return The result of a/b, in ray
   **/
  function rayDiv(uint256 a, uint256 b) internal pure returns (uint256) {
    require(b != 0, MathErrors.MATH_DIVISION_BY_ZERO);
    uint256 halfB = b / 2;

    require(a <= (type(uint256).max - halfB) / RAY, MathErrors.MATH_MULTIPLICATION_OVERFLOW);

    return (a * RAY + halfB) / b;
  }

  /**
   * @dev Casts ray down to wad
   * @param a Ray
   * @return a casted to wad, rounded half up to the nearest wad
   **/
  function rayToWad(uint256 a) internal pure returns (uint256) {
    uint256 halfRatio = WAD_RAY_RATIO / 2;
    uint256 result = halfRatio + a;
    require(result >= halfRatio, MathErrors.MATH_ADDITION_OVERFLOW);

    return result / WAD_RAY_RATIO;
  }

  /**
   * @dev Converts wad up to ray
   * @param a Wad
   * @return a converted in ray
   **/
  function wadToRay(uint256 a) internal pure returns (uint256) {
    uint256 result = a * WAD_RAY_RATIO;
    require(result / WAD_RAY_RATIO == a, MathErrors.MATH_MULTIPLICATION_OVERFLOW);
    return result;
  }
}

contracts/YieldFiAdmin.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.12;

import "./WadRayMath.sol";

contract YieldFiAdmin {
    using WadRayMath for uint256;

    uint256 public constant WAD = 1e18;

    mapping(address => uint256) public collateralFactors;
    mapping(address => uint256) public liquidationThresholds;
    mapping(address => uint256) public reserveFactors;
    mapping(address => address) public priceOracles;

    event CollateralFactorUpdated(address indexed reserve, uint256 collateralFactor);
    event LiquidationThresholdUpdated(address indexed reserve, uint256 liquidationThreshold);
    event ReserveFactorUpdated(address indexed reserve, uint256 reserveFactor);
    event OracleUpdated(address indexed reserve, address indexed oracle);

    constructor(
        address _ethOracle,
        address _wbtcOracle
    ) {
        priceOracles[address(0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeeeeEEeE)] = _ethOracle;
        priceOracles[address(0x2260FAC5E5542a773Aa44fBCfeDf7C11C599)] = _wbtcOracle;
    }

    function setCollateralFactor(address _reserve, uint256 _collateralFactor) external {
        require(_collateralFactor <= WAD, "Collateral factor must be less than or equal to 1");
        collateralFactors[_reserve] = _collateralFactor;
        emit CollateralFactorUpdated(_reserve, _collateralFactor);
    }

    function setLiquidationThreshold(address _reserve, uint256 _liquidationThreshold) external {
        require(_liquidationThreshold <= WAD, "Liquidation threshold must be less than or equal to 1");
        liquidationThresholds[_reserve] = _liquidationThreshold;
        emit LiquidationThresholdUpdated(_reserve, _liquidationThreshold);
    }

    function setReserveFactor(address _reserve, uint256 _reserveFactor) external {
        require(_reserveFactor <= WAD, "Reserve factor must be less than or equal to 1");
        reserveFactors[_reserve] = _reserveFactor;
        emit ReserveFactorUpdated(_reserve, _reserveFactor);
    }

    function setOracle(address _reserve, address _oracle) external {
        priceOracles[_reserve] = _oracle;
        emit OracleUpdated(_reserve, _oracle);
    }
}

contracts/YieldFiCalculations.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.12;

import "./WadRayMath.sol";
import "./YieldFiCollateral.sol";
import "./YieldFiFlashLoan.sol";
import "./YieldFiAdmin.sol";
import "./AggregatorV3Interface.sol";
import "./IERC20.sol";

contract YieldFiCalculations {
    using WadRayMath for uint256;

    YieldFiCollateral public yieldFiCollateralInterface;
    YieldFiFlashLoan public yieldFiFlashLoanInterface;
    YieldFiAdmin public yieldFiAdminInterface;

    uint256 public constant WADNew = 1e18;
    mapping(address => mapping(address => uint256)) public scaledUserBalance;
    mapping(address => uint256) public cumulatedLiquidityIndex;

    constructor(
        YieldFiCollateral _collateral,
        YieldFiFlashLoan _flashLoan,
        YieldFiAdmin _admin
    ) {
        yieldFiCollateralInterface = _collateral;
        yieldFiFlashLoanInterface = _flashLoan;
        yieldFiAdminInterface = _admin;
    }

    function calculateHealthFactor(address _user) public view returns (uint256) {
        uint256 totalCollateralValue = 0;
        uint256 totalBorrowValue = 0;

        address[] memory reserves = yieldFiFlashLoanInterface.getAllReserves();

        for (uint256 i = 0; i < reserves.length; i++) {
            address reserve = reserves[i];
            if (yieldFiCollateralInterface.isCollateralEnabled(_user, reserve)) {
                uint256 collateralValue = calculateCollateralValue(_user, reserve);
                totalCollateralValue += collateralValue;
            }
            totalBorrowValue += calculateBorrowValue(_user, reserve);
        }

        if (totalBorrowValue == 0) {
            return type(uint256).max;
        }

        return (totalCollateralValue * WADNew) / totalBorrowValue;
    }

    function calculateCollateralValue(address _user, address _reserve) internal view returns (uint256) {
        uint256 scaledBalance = scaledUserBalance[_user][_reserve];
        uint256 liquidityIndex = cumulatedLiquidityIndex[_reserve];
        uint256 price = getAssetPrice(_reserve);
        uint256 collateralFactor = yieldFiAdminInterface.collateralFactors(_reserve);

        return (scaledBalance * liquidityIndex * price * collateralFactor) / (WADNew * WADNew);
    }

    function calculateBorrowValue(address _user, address _reserve) internal view returns (uint256) {
        uint256 borrowBalance = yieldFiCollateralInterface.borrowBalance(_user, _reserve);
        uint256 variableBorrowIndex = yieldFiCollateralInterface.cumulatedVariableBorrowIndex(_reserve);
        uint256 price = getAssetPrice(_reserve);
        return (borrowBalance * variableBorrowIndex * price) / WADNew;
    }

    function calculateCollateralAmountToSeize(
        address _reserve,
        address _collateral,
        uint256 _debtToCover
    ) public view returns (uint256) {
        uint256 collateralPrice = getAssetPrice(_collateral);
        uint256 debtPrice = getAssetPrice(_reserve);
        uint256 collateralDecimals = IERC20(_collateral).decimals();
        uint256 debtDecimals = IERC20(_reserve).decimals();
        uint256 maxCollateralToSeize = (_debtToCover * debtPrice * 10**collateralDecimals) / (collateralPrice * debtDecimals * yieldFiAdminInterface.liquidationThresholds(_collateral)) / WADNew;
        return maxCollateralToSeize;
    }

    function getAssetPrice(address _reserve) internal view returns (uint256) {
        AggregatorV3Interface oracle = AggregatorV3Interface(yieldFiAdminInterface.priceOracles(_reserve));
        (, int256 price, , , ) = oracle.latestRoundData();
        return uint256(price);
    }
}

contracts/YieldFiCollateral.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.12;

import "./WadRayMath.sol";
import "./IYieldFiInteractions.sol";
import "./IDebtToken.sol";
import "./IERC20.sol";
import "./YieldFiAdmin.sol";

contract YieldFiCollateral {
    using WadRayMath for uint256;

    YieldFiAdmin public yieldFiAdmin;
    address public yieldFiCore;

    mapping(address => mapping(address => bool)) public isCollateralEnabled;
    mapping(address => mapping(address => uint256)) public userBalance;
    mapping(address => mapping(address => uint256)) public borrowBalance;
    mapping(address => mapping(address => uint256)) public principalBorrowBalance;
    mapping(address => address) public variableDebtTokenContracts;
    mapping(address => uint256) public totalBorrow;
    mapping(address => uint256) public cumulatedVariableBorrowIndex;

    event UseAsCollateral(address indexed user, address indexed reserve, bool useAsCollateral);
    event RepayWithCollateral(address indexed user, address indexed reserve, uint256 amount, address indexed onBehalfOf);
    event DelegateBorrowingPower(address indexed user, address indexed to, address indexed reserve, uint256 amount);
    event Liquidate(address indexed borrower, address indexed reserve, uint256 amount, address indexed collateral, uint256 collateralAmount);

    constructor(YieldFiAdmin _admin) {
        yieldFiAdmin = _admin;
    }

    function useAsCollateral(address _reserve, bool _useAsCollateral) public {
        require(userBalance[msg.sender][_reserve] > 0, "No balance to use as collateral");

        isCollateralEnabled[msg.sender][_reserve] = _useAsCollateral;

        emit UseAsCollateral(msg.sender, _reserve, _useAsCollateral);
    }

    function repayWithCollateral(address _reserve, uint256 _amount, address _onBehalfOf) public {
        require(_amount > 0, "Amount must be greater than zero");
        require(borrowBalance[_onBehalfOf][_reserve] >= _amount, "Insufficient borrow balance");
        require(isCollateralEnabled[_onBehalfOf][_reserve], "Collateral not enabled");

        IDebtToken(variableDebtTokenContracts[_reserve]).safeTransferFrom(msg.sender, address(this), _amount);

        uint256 scaledAmount = _amount.rayDiv(cumulatedVariableBorrowIndex[_reserve]);
        borrowBalance[_onBehalfOf][_reserve] -= scaledAmount;
        principalBorrowBalance[_onBehalfOf][_reserve] -= scaledAmount;
        totalBorrow[_reserve] -= scaledAmount;

        IDebtToken(variableDebtTokenContracts[_reserve]).burn(_onBehalfOf, _amount);

        emit RepayWithCollateral(msg.sender, _reserve, _amount, _onBehalfOf);
    }

    function delegateBorrowingPower(address _to, address _reserve, uint256 _amount) public {
        require(_amount > 0, "Amount must be greater than zero");
        require(borrowBalance[msg.sender][_reserve] >= _amount, "Insufficient borrow balance");

        borrowBalance[_to][_reserve] += _amount;
        borrowBalance[msg.sender][_reserve] -= _amount;

        emit DelegateBorrowingPower(msg.sender, _to, _reserve, _amount);
    }
}

contracts/YieldFiCore.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.12;

import "./YieldFiCollateral.sol";
import "./YieldFiFlashLoan.sol";
import "./YieldFiCalculations.sol";
import "./YieldFiAdmin.sol";
import "./ReentrancyGuard.sol";
import "./SafeERC20.sol";
import "./AggregatorV3Interface.sol";
import "./ILendingPool.sol";
import "./IAToken.sol";
import "./IDebtToken.sol";
import "./AToken.sol";
import "./DebtToken.sol";
import "./WadRayMath.sol";
import "./AToken.sol";
import "./DebtToken.sol";

contract YieldFiCore is ReentrancyGuard, YieldFiCollateral, YieldFiFlashLoan, YieldFiCalculations, YieldFiAdmin {
    using SafeERC20 for IERC20;
    using WadRayMath for uint256;

    uint256 public constant RAY = 1e27;

    mapping(address => uint256) public normalizedIncomeIndex; // New mapping for normalized income index

    mapping(address => address) public aTokenContracts;

    event Deposit(address indexed user, address indexed reserve, uint256 amount);
    event Withdraw(address indexed user, address indexed reserve, uint256 amount);
    event Borrow(address indexed user, address indexed reserve, uint256 amount);
    event Repay(address indexed user, address indexed reserve, uint256 amount);
    event InterestRateUpdated(address indexed reserve, uint256 liquidityRate, uint256 stableBorrowRate, uint256 variableBorrowRate);

    mapping(address => uint256) public totalSupply;

    constructor(
        address _ethOracle,
        address _wbtcOracle,
        address _lendingPoolAddress
    )
    YieldFiAdmin(_ethOracle, _wbtcOracle)
    YieldFiFlashLoan(_lendingPoolAddress)
    YieldFiCollateral(YieldFiAdmin(address(this)))
    YieldFiCalculations(YieldFiCollateral(address(this)), YieldFiFlashLoan(address(this)), YieldFiAdmin(address(this)))
    {}

    function deposit(address _reserve, uint256 _amount) external nonReentrant {
        require(_amount > 0, "Amount must be greater than zero");

        IERC20(_reserve).safeTransferFrom(msg.sender, address(this), _amount);
        updateState(_reserve);

        uint256 scaledAmount = _amount.rayDiv(normalizedIncomeIndex[_reserve]); // Use normalized income index
        userBalance[msg.sender][_reserve] += _amount;
        scaledUserBalance[msg.sender][_reserve] += scaledAmount;
        totalSupply[_reserve] += _amount;

        address aToken = aTokenContracts[_reserve];
        if (aToken == address(0)) {
            aToken = address(new AToken("Aave Token", "aToken", IERC20(_reserve).decimals(), _reserve, address(this)));
            aTokenContracts[_reserve] = aToken;
        }

        IAToken(aToken).mint(msg.sender, _amount);

        emit Deposit(msg.sender, _reserve, _amount);
    }

    function withdraw(address _reserve, uint256 _amount) external nonReentrant {
        require(_amount > 0, "Amount must be greater than zero");
        require(userBalance[msg.sender][_reserve] >= _amount, "Insufficient balance");

        updateState(_reserve);

        uint256 scaledAmount = _amount.rayDiv(normalizedIncomeIndex[_reserve]); // Use normalized income index
        userBalance[msg.sender][_reserve] -= _amount;
        scaledUserBalance[msg.sender][_reserve] -= scaledAmount;
        totalSupply[_reserve] -= _amount;

        address aToken = aTokenContracts[_reserve];
        require(aToken != address(0), "aToken not set");
        IAToken(aToken).burn(msg.sender, _amount);

        IERC20(_reserve).safeTransfer(msg.sender, _amount);

        emit Withdraw(msg.sender, _reserve, _amount);
    }

    function borrow(address _reserve, uint256 _amount) external nonReentrant {
        require(_amount > 0, "Amount must be greater than zero");
        require(totalBorrow[_reserve] + _amount <= totalSupply[_reserve], "Insufficient liquidity");

        updateState(_reserve);

        borrowBalance[msg.sender][_reserve] += _amount;
        principalBorrowBalance[msg.sender][_reserve] += _amount;
        totalBorrow[_reserve] += _amount;

        address variableDebtToken = variableDebtTokenContracts[_reserve];
        if (variableDebtToken == address(0)) {
            variableDebtToken = address(new DebtToken("Variable Debt Token", "vDebtToken", IERC20(_reserve).decimals(), _reserve, address(this)));
            variableDebtTokenContracts[_reserve] = variableDebtToken;
        }

        IDebtToken(variableDebtToken).mint(msg.sender, _amount);

        IERC20(_reserve).safeTransfer(msg.sender, _amount);

        emit Borrow(msg.sender, _reserve, _amount);
    }

    function repay(address _reserve, uint256 _amount) external nonReentrant {
        require(_amount > 0, "Amount must be greater than zero");
        require(borrowBalance[msg.sender][_reserve] >= _amount, "Insufficient borrow balance");

        IERC20(_reserve).safeTransferFrom(msg.sender, address(this), _amount);

        updateState(_reserve);

        uint256 scaledAmount = _amount.rayDiv(normalizedIncomeIndex[_reserve]); // Use normalized income index
        borrowBalance[msg.sender][_reserve] -= scaledAmount;
        principalBorrowBalance[msg.sender][_reserve] -= _amount;
        totalBorrow[_reserve] -= scaledAmount;

        address variableDebtToken = variableDebtTokenContracts[_reserve];
        require(variableDebtToken != address(0), "VariableDebtToken not set");

        IDebtToken(variableDebtToken).burn(msg.sender, _amount);

        emit Repay(msg.sender, _reserve, _amount);
    }

    function liquidate(address _borrower, address _reserve, uint256 _amount, address _collateral) external nonReentrant {
        require(_amount > 0, "Amount must be greater than zero");
        require(borrowBalance[_borrower][_reserve] >= _amount, "Insufficient borrow balance");
        require(isCollateralEnabled[_borrower][_collateral], "Collateral not enabled");

        uint256 healthFactor = calculateHealthFactor(_borrower);
        require(healthFactor < liquidationThresholds[_collateral], "Borrower is healthy");

        uint256 collateralAmount = calculateCollateralAmountToSeize(_reserve, _collateral, _amount);

        address collateralDebtToken = variableDebtTokenContracts[_collateral];
        require(collateralDebtToken != address(0), "Collateral DebtToken not set");

        IDebtToken(collateralDebtToken).safeTransferFrom(_borrower, msg.sender, collateralAmount);

        uint256 scaledAmount = _amount.rayDiv(normalizedIncomeIndex[_reserve]); // Use normalized income index
        borrowBalance[_borrower][_reserve] -= scaledAmount;
        principalBorrowBalance[_borrower][_reserve] -= _amount;
        totalBorrow[_reserve] -= scaledAmount;

        address variableDebtToken = variableDebtTokenContracts[_reserve];
        require(variableDebtToken != address(0), "VariableDebtToken not set");

        IDebtToken(variableDebtToken).burn(_borrower, _amount);

        emit Liquidate(_borrower, _reserve, _amount, _collateral, collateralAmount);
    }

    function updateState(address _reserve) internal {
        (
            ,
            uint128 liquidityIndex,
            uint128 variableBorrowIndex,
            uint128 currentLiquidityRate,
            uint128 currentVariableBorrowRate,
            ,
            uint40 lastUpdateTimestamp,
            address aTokenAddress,
            address variableDebtTokenAddress,
            ,
        ) = ILendingPool(lendingPoolAddress).getReserveData(_reserve);

        if (lastUpdateTimestamp == block.timestamp) {
            return;
        }

        cumulatedLiquidityIndex[_reserve] = uint256(liquidityIndex);
        cumulatedVariableBorrowIndex[_reserve] = uint256(variableBorrowIndex);

        // Update normalized income index
        uint256 newNormalizedIncomeIndex = normalizedIncomeIndex[_reserve].rayMul(uint256(currentLiquidityRate).rayDiv(RAY) + RAY);
        normalizedIncomeIndex[_reserve] = newNormalizedIncomeIndex;

        if (aTokenAddress == address(0)) {
            aTokenAddress = address(new AToken("Aave Token", "aToken", IERC20(_reserve).decimals(), _reserve, address(this)));
            aTokenContracts[_reserve] = aTokenAddress;
        }

        if (variableDebtTokenAddress == address(0)) {
            variableDebtTokenAddress = address(new DebtToken("Variable Debt Token", "vDebtToken", IERC20(_reserve).decimals(), _reserve, address(this)));
            variableDebtTokenContracts[_reserve] = variableDebtTokenAddress;
        }

        emit InterestRateUpdated(_reserve, currentLiquidityRate, 0, currentVariableBorrowRate);
    }
}

contracts/YieldFiFlashLoan.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.12;

import "./IFlashLoanReceiver.sol";
import "./ILendingPool.sol";
import "./SafeERC20.sol";

contract YieldFiFlashLoan is IFlashLoanReceiver {
    using SafeERC20 for IERC20;

    address public lendingPoolAddress;

    constructor(address _lendingPoolAddress) {
        lendingPoolAddress = _lendingPoolAddress;
    }

    event FlashLoan(address indexed user, address indexed reserve, uint256 amount);

    function flashLoan(address _receiver, address _reserve, uint256 _amount) external {
        require(_amount > 0, "Amount must be greater than zero");

        address[] memory assets = new address[](1);
        assets[0] = _reserve;
        uint256[] memory amounts = new uint256[](1);
        amounts[0] = _amount;
        uint256[] memory modes = new uint256[](1);
        modes[0] = 0;

        ILendingPool(lendingPoolAddress).flashLoan(_receiver, assets, amounts, modes, msg.sender, "", 0);

        emit FlashLoan(msg.sender, _reserve, _amount);
    }

    function executeOperation(
        address[] calldata assets,
        uint256[] calldata amounts,
        uint256[] calldata premiums,
        address initiator,
        bytes calldata
    ) external override returns (bool) {
        for (uint256 i = 0; i < assets.length; i++) {
            uint256 totalDebt = amounts[i] + premiums[i];
            require(IERC20(assets[i]).balanceOf(address(this)) >= totalDebt, "Flashloan not repaid");

            IERC20(assets[i]).safeTransfer(initiator, totalDebt);
        }

        return true;
    }

    function getAllReserves() public view returns (address[] memory) {
        return ILendingPool(lendingPoolAddress).getReservesList();
    }
}

